import{_ as e,c as t,o as l,ag as o}from"./chunks/framework.oP1PDRBo.js";const u=JSON.parse('{"title":"读《活着》有感","description":"","frontmatter":{"title":"读《活着》有感","date":"2024-01-28T00:00:00.000Z","tags":["读书","感悟"],"categories":["读书笔记"]},"headers":[],"relativePath":"blogs/reading-001.md","filePath":"blogs/reading-001.md","lastUpdated":1742362214000}'),i={name:"blogs/reading-001.md"};function r(s,a,d,n,c,h){return l(),t("div",null,a[0]||(a[0]=[o('<h1 id="《活着》读后感" tabindex="-1">《活着》读后感 <a class="header-anchor" href="#《活着》读后感" aria-label="Permalink to &quot;《活着》读后感&quot;">​</a></h1><h2 id="📚-关于这本书" tabindex="-1">📚 关于这本书 <a class="header-anchor" href="#📚-关于这本书" aria-label="Permalink to &quot;📚 关于这本书&quot;">​</a></h2><p>最近重读了余华的《活着》，这是一部让人深思的作品。主人公福贵的一生，就像一面镜子，照出了生命的意义和人性的光辉。</p><h2 id="💭-感悟片段" tabindex="-1">💭 感悟片段 <a class="header-anchor" href="#💭-感悟片段" aria-label="Permalink to &quot;💭 感悟片段&quot;">​</a></h2><blockquote><p>&quot;人是为活着本身而活着的，而不是为了活着之外的任何事物所活着。&quot;</p></blockquote><p>这句话让我想了很久。在这个快节奏的时代，我们常常忘记了最简单的道理：活着本身就是一种意义。</p><h2 id="印象深刻的情节" tabindex="-1">印象深刻的情节 <a class="header-anchor" href="#印象深刻的情节" aria-label="Permalink to &quot;印象深刻的情节&quot;">​</a></h2><ol><li>福贵输掉家产的那一段</li><li>有庆死去时的场景</li><li>凤霞和二喜的爱情</li><li>苦难中的坚强与希望</li></ol><h2 id="🌟-个人思考" tabindex="-1">🌟 个人思考 <a class="header-anchor" href="#🌟-个人思考" aria-label="Permalink to &quot;🌟 个人思考&quot;">​</a></h2><p>读完这本书，让我对生活有了新的认识：</p><ul><li>生活中的苦难不应该成为我们放弃的理由</li><li>平凡的生活中也处处充满温情</li><li>家人之间的爱才是最珍贵的</li></ul><div class="tip custom-block"><p class="custom-block-title">推荐理由</p><p>这是一本值得反复阅读的好书，每次读都会有新的感悟。</p></div><h2 id="📖-读书笔记" tabindex="-1">📖 读书笔记 <a class="header-anchor" href="#📖-读书笔记" aria-label="Permalink to &quot;📖 读书笔记&quot;">​</a></h2><p>今天的阅读时间：2小时 阅读环境：咖啡馆角落 背景音乐：《月光》</p><blockquote><p>有时候，静静地读一本好书，就是最好的心灵对话。</p></blockquote><p>今日心情：平静 🍃 推荐指数：⭐⭐⭐⭐⭐</p>',16)]))}const b=e(i,[["render",r]]);export{u as __pageData,b as default};
